mapper.select:
select username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select username, userpwd, userid, email, joindate, score
from ssafy_member
where userid = #{userid}

mapper.update:
update ssafy_member
set username = #{username}, userpwd = #{userpwd}, email = #{email}, score = #{score}
where userid = #{userid}
